import F2 from '@antv/f2';
import _ from 'lodash';

const data = [{
  time: '周一',
  tem: 6.9,
  city: 'tokyo'
}, {
  time: '周二',
  tem: 9.5,
  city: 'tokyo'
}, {
  time: '周三',
