  tem: -22,
  city: 'newYork'
}, {
  time: '周六',
  tem: -24.8,
  city: 'newYork'
}, {
  time: '周日',
  tem: -24.1,
  city: 'newYork'
}, {
  time: '周一',
  tem: 2.6,
  city: 'berlin'
}, {
  time: '周二',
  tem: 3.5,
  city: 'berlin'
}, {
  time: '周三',
  tem: 8.4,
  city: 'berlin'
