      nice: false
    }
  }); // 设定极坐标系

  chart.coord('polar', {
    transposed: true,
    innerRadius: 0.8,
    startAngle: -Math.PI,
    endAngle: 0
  });
  chart.axis(false); // 绘制两个条形，分别作为背景和实际的百分比进度。

  chart.interval().position('const*expect').shape('polar-tick').size(10).color('rgba(80, 143, 255, 0.95)').animate(false);
  chart.interval().position('const*actual').shape('polar-tick').size(10).color('#fff').animate({
    appear: {
      duration: 1100,
      easing: 'linear',
      animation: function animation(shape, animateCfg) {
        var startAngle = shape.attr('startAngle');
        var endAngle = shape.attr('endAngle');

        if (startAngle > endAngle) {
          endAngle += Math.PI * 2;
        }

        shape.attr('endAngle', startAngle);
        shape.animate().to(Util.mix({
          attrs: {
            endAngle: endAngle
          }
        }, animateCfg)).onUpdate(function (frame) {
          var textEl = document.querySelector('#text');
          if (textEl) textEl.innerHTML = parseInt(frame * 75) + '%';
        });
